import React, {useState} from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Cloud(props) {
    const [color, setColor] = useState("#F5F5F5");
    const colors = ["#BD1465",  "#F5F5F5", "#364F6B", "#F0F0F0", "#B9BBDF","#AEE1E1", "#D3E0DC"];
    const cloudStyle = {
        color: color
    };

    function setRandomColor() {
        const randColor = Math.floor(Math.random()*colors.length);
        setColor(colors[randColor]);
    }

    let cloudClass = "fa-solid fa-cloud fa-7x cloud " + props.type;
    // <img draggable={false} style={{border: "5px solid " + {color}}} className ={cloudClass} src="media/cloud2.svg" alt="cloud" onClick={setRandomColor} />
    // <span aria-label="Twitter icon" className="fa-brands fa-twitter social-icon footer-image fa-2x"></span>
    return <Row><Col xs={{span: 4, offset: 6}}><span style={{color: {color}}} className ={cloudClass} onClick={setRandomColor}></span></Col></Row>;
}

export default Cloud;
