import React, {useState} from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Cloud(props) {
    const [color, setColor] = useState("#F5F5F5");
    const colors = ["#BD1465",  "#F5F5F5", "#364F6B"];

    function setRandomColor() {
        const randColor = Math.floor(Math.random()*colors.length);
        setColor(colors[randColor]);
    }

    let cloudClass = "cloud " + props.type;
    return <Row><Col xs={{span: 4, offset: 6}}><img style={{border: "5px solid " + {color}}} className ={cloudClass} src="media/cloud2.svg" alt="cloud" onClick={setRandomColor} /></Col></Row>;
}

export default Cloud;
