import React, {useState} from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Cloud(props) {
    const [cloudColor, setColor] = useState("#F5F5F5");
    const colors = ["#BD1465",  "#F5F5F5", "#364F6B", "#F0F0F0","#AEE1E1", "#D3E0DC"];

    function setRandomColor() {
        const randColor = Math.floor(Math.random()*colors.length);
        setColor(colors[randColor]);
    }

    let cloudClass = "fa-solid fa-cloud fa-7x cloud " + props.type;
    return <Row><Col xs={{span: 4, offset: 6}}><span style={{color: cloudColor}} className ={cloudClass} onClick={setRandomColor} /></Col></Row>;
}

export default Cloud;
