import React, {useState} from "react";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Cloud(props) {
    const [isLined, setLined] = useState(false);
    const [color, setColor] = useState("#F5F5F5");
    const colors = [];

    let cloudClass = "cloud " + props.type;
    return <Row><Col xs={{span: 4, offset: 6}}><img style={{border: "5px solid " + {color}}} className ={cloudClass} src="media/cloud2.svg" alt="cloud" onClick={(prev)=>setLined(!prev)} /></Col></Row>;
}

export default Cloud;
